INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('1aab5692-85a5-4151-b67c-8c2c93353916', 'OpenStack 用户项目扫描', 1, 'LowRisk', 'OpenStack 检测用户是否绑定主项目，已绑定视为“合规”，否则属于“不合规”', 'policies:\n    # 检测用户是否绑定主项目，已绑定视为“合规”，否则属于“不合规”\n    - name: openstack-filter-user-project\n      resource: openstack.user\n      filters:\n        - type: project\n          default_project_id: ${{default_project_id}}', '[{\"key\":\"default_project_id\",\"name\":\"项目ID\",\"defaultValue\":\"\'\'\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('311daee9-3c15-4b19-8fb7-97823c57eb23', 'OpenStack 模板类型扫描', 1, 'MediumRisk', 'OpenStack 检测模板实例类型是否为公有，并且内存/CPU/磁盘大于指定最低值，符合条件视为“合规”，否则属于“不合规”', 'policies:\n    # 检测模板实例类型是否为公有，并且内存/CPU/磁盘大于指定最低值，符合条件视为“合规”，否则属于“不合规”\n    - name: openstack-flavor\n      resource: openstack.flavor\n      filters:\n        - type: system\n          is_public: ${{is_public}}\n          ram: ${{ram}}\n          vcpus: ${{vcpus}}\n          disk: ${{disk}}', '[{\"key\":\"is_public\",\"name\":\"是否为公有\",\"defaultValue\":\"true\",\"required\":true},{\"key\":\"ram\",\"name\":\"内存大小\",\"defaultValue\":\"512\",\"required\":true},{\"key\":\"vcpus\",\"name\":\"CPU\",\"defaultValue\":\"1\",\"required\":true},{\"key\":\"disk\",\"name\":\"磁盘大小\",\"defaultValue\":\"1\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('5673475f-c43d-432c-a6a6-f29479493b4d', 'OpenStack 卷加密扫描', 1, 'HighRisk', 'OpenStack 检测卷是否加密，加密视为“合规”，否则属于“不合规”', 'policies:\n    # 检测卷是否加密，加密视为“合规”，否则属于“不合规”\n    - name: openstack-volume\n      resource: openstack.volume\n      filters:\n        - type: status\n          is_encrypted: ${{is_encrypted}}', '[{\"key\":\"is_encrypted\",\"name\":\"是否加密\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('5c4d5e15-ce0a-4322-8406-47e8bcb10bf4', 'OpenStack 实例时间扫描', 1, 'LowRisk', 'OpenStack 检测实例运行时间是否大于n天，小于n天视为“合规”，否则属于“不合规”', 'policies:\n    # 检测实例运行时间是否大于n天，小于n天视为“合规”，否则属于“不合规”\n    - name: openstack-server-age\n      resource: openstack.server\n      filters:\n        - type: age\n          op: ${{op}}\n          days: ${{days}}', '[{\"key\":\"days\",\"name\":\"天\",\"defaultValue\":\"90\",\"required\":true},{\"key\":\"op\",\"name\":\"大于(小于lt)\",\"defaultValue\":\"gt\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('5f6c1aed-8c46-48e6-b48d-2cf06568831f', 'OpenStack 用户角色扫描', 1, 'LowRisk', 'OpenStack 检测项目中的用户是否为管理员角色，非管理员角色视为“合规”，否则属于“不合规”', 'policies:\n    # 检测项目中的用户是否为管理员角色，非管理员角色视为“合规”，否则属于“不合规”\n    - name: openstack-filter-user-role\n      resource: openstack.user\n      filters:\n        - type: role\n          role_name: admin\n          project_name: ${{project_name}}\n          system_scope: ${{system_scope}}', '[{\"key\":\"project_name\",\"name\":\"项目名称\",\"defaultValue\":\"admin\",\"required\":true},{\"key\":\"system_scope\",\"name\":\"是否\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('6359bdcd-0d1d-4e43-817c-cc338f07a065', 'OpenStack 实例镜像扫描', 1, 'LowRisk', 'OpenStack 检测镜像是否属于私有并运行状态的实例，符合条件视为“合规”，否则属于“不合规”', 'policies:\n    # 检测镜像是否属于私有并运行状态的实例，符合条件视为“合规”，否则属于“不合规”\n    - name: openstack-server-image\n      resource: openstack.server\n      filters:\n        - not:\n          - type: image\n            #image_name: centos\n            visibility: ${{visibility}}\n            status: ${{status}}', '[{\"key\":\"visibility\",\"name\":\"可见性\",\"defaultValue\":\"private\",\"required\":true},{\"key\":\"status\",\"name\":\"运行状态\",\"defaultValue\":\"active\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('63dedb65-0237-4402-8a5c-1d2137840fdd', 'OpenStack 路由状态扫描', 1, 'LowRisk', 'OpenStack 检测路由状态与高可用状态，符合条件视为“合规”，否则属于“不合规”', 'policies:\n    # 检测路由状态与高可用状态，符合条件视为“合规”，否则属于“不合规”\n    - name: openstack-router\n      resource: openstack.router\n      filters:\n        - not:\n          - type: system\n            status: ${{status}}\n            ha: ${{ha}}', '[{\"key\":\"status\",\"name\":\"网络状态\",\"defaultValue\":\"ACTIVE\",\"required\":true},{\"key\":\"ha\",\"name\":\"是否为高可用\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('6bbd42bc-a251-498a-8b19-8de8c55e32aa', 'OpenStack 卷状态扫描', 1, 'LowRisk', 'OpenStack 检测卷是否关联实例，关联视为“合规”，否则属于“不合规”', 'policies:\n    # 检测卷是否关联实例，关联视为“合规”，否则属于“不合规”\n    - name: openstack-volume\n      resource: openstack.volume\n      filters:\n        - type: status\n          volume_status: ${{volume_status}}\n          system_scope: ${{system_scope}}', '[{\"key\":\"volume_status\",\"name\":\"卷状态\",\"defaultValue\":\"\'in-use\'\",\"required\":true},{\"key\":\"system_scope\",\"name\":\"是否关联实例\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('701ae534-4c13-45a2-8a72-e78afdea4d2b', 'OpenStack SecurityGroups高危安全组扫描', 1, 'HighRisk', 'OpenStack  检测安全组是否开启风险端口，未开启视为“合规”，否则属于“不合规”', 'policies:\n  # 扫描开放以下高危端口的安全组：\n  #(20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500)\n  - name: openstack-security-groups\n    resource: openstack.security-groups\n    description: |\n      Add Filter all security groups, filter ports\n      [20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500]\n      on 0.0.0.0/0 or\n      [20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\n      on ::/0 (IPv6)\n    filters:\n        - or:\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv4_port}}\n              Cidr: \"0.0.0.0/0\"\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv6_port}}\n              Cidr: \"::/0\"', '[{\"key\":\"ipv4_port\",\"name\":\"ipv4端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true},{\"key\":\"ipv6_port\",\"name\":\"ipv6端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('ab3306d0-e734-4977-a462-42de0e1cf263', 'OpenStack 实例标签扫描', 1, 'LowRisk', 'OpenStack 检测实例标签是否属于默认值，属于视为“合规”，否则属于“不合规”', 'policies:\n    # 检测实例标签是否属于默认值，属于视为“合规”，否则属于“不合规”\n    - name: openstack-server-tags\n      resource: openstack.server\n      filters:\n        - type: tags\n          tags:\n          - key: ${{key1}}\n            value: ${{value1}}\n          - key: ${{key2}}\n            value: ${{value2}}\n          op: any', '[{\"key\":\"key1\",\"name\":\"key1\",\"defaultValue\":\"absent\",\"required\":true},{\"key\":\"value1\",\"name\":\"value1\",\"defaultValue\":\"absent\",\"required\":true},{\"key\":\"key2\",\"name\":\"key2\",\"defaultValue\":\"absent\",\"required\":true},{\"key\":\"value2\",\"name\":\"value2\",\"defaultValue\":\"absent\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('b8e09c5a-fecc-4400-b831-d7f75f9cee01', 'OpenStack 网络状态扫描', 1, 'LowRisk', 'OpenStack 检测网络状态与共享状态，符合条件视为“合规”，否则属于“不合规”', 'policies:\n    # 检测网络状态与共享状态，符合条件视为“合规”，否则属于“不合规”\n    - name: openstack-network\n      resource: openstack.network\n      filters:\n        - not:\n          - type: system\n            status: ${{status}}\n            shared: ${{shared}}\n            port_security_enabled: ${{port_security_enabled}}', '[{\"key\":\"status\",\"name\":\"网络状态\",\"defaultValue\":\"ACTIVE\",\"required\":true},{\"key\":\"shared\",\"name\":\"共享状态\",\"defaultValue\":\"false\",\"required\":true},{\"key\":\"port_security_enabled\",\"name\":\"端口安全是否开启\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('bf9a87c9-f709-4f84-8a86-5afde3eb40c3', 'OpenStack 项目成员扫描', 1, 'LowRisk', 'OpenStack 检测项目是否分配成员，已分配视为“合规”，否则属于“不合规”', 'policies:\n    # 检测项目是否分配成员，已分配视为“合规”，否则属于“不合规”\n    - name: openstack-project-user\n      resource: openstack.project\n      filters:\n        - type: user\n          system_scope: ${{system_scope}}', '[{\"key\":\"system_scope\",\"name\":\"是否包含项目成员\",\"defaultValue\":\"true\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('c9162947-90f2-41c9-8df8-2d2244bb6f1d', 'OpenStack SecurityGroups安全组配置扫描', 1, 'HighRisk', 'OpenStack  账号下安全组配置不为“0.0.0.0/0”，视为“合规”，否则属于“不合规”', 'policies:\n    # 账号下安全组配置不为“0.0.0.0/0”，视为“合规”，否则属于“不合规”\n    - name: openstack-sg-source-cidr-ip\n      resource: openstack.security-groups\n      filters:\n        - type: source-cidr-ip\n          value: ${{value}}', '[{\"key\":\"value\",\"name\":\"目标IP地址段\",\"defaultValue\":\"\\\"0.0.0.0/0\\\"\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('ccf64ae6-7f99-40fe-b45f-d86a4b63bdcc', 'OpenStack 实例类型扫描', 1, 'LowRisk', 'OpenStack 检测实例的模板类型是否为公有，并且内存/CPU/磁盘大于指定最低值，符合条件视为“合规”，否则属于“不合规”', 'policies:\n    # 检测实例的模板类型是否为公有，并且内存/CPU/磁盘大于指定最低值，符合条件视为“合规”，否则属于“不合规”\n    - name: openstack-server-flavor\n      resource: openstack.server\n      filters:\n        - type: flavor\n          is_public: ${{is_public}}\n          ram: ${{ram}}\n          vcpus: ${{vcpus}}\n          disk: ${{disk}}', '[{\"key\":\"is_public\",\"name\":\"是否为公有\",\"defaultValue\":\"true\",\"required\":true},{\"key\":\"ram\",\"name\":\"内存大小\",\"defaultValue\":\"512\",\"required\":true},{\"key\":\"vcpus\",\"name\":\"CPU\",\"defaultValue\":\"1\",\"required\":true},{\"key\":\"disk\",\"name\":\"磁盘大小\",\"defaultValue\":\"1\",\"required\":true}]', 'fit2cloud-openstack-plugin', 'OpenStack', 'openstack.png', concat(unix_timestamp(now()), '006'), 1, 'custodian');
