INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`) VALUES ('9929cc2a-7a9b-4643-a813-0e4359b09eb0', 'AWS 2.0 Logging', 1, 'HighRisk', 'AWS 2.0 Logging - CIS only - [group2]', '2.0 Logging - CIS only - [group2] **********************************\n\n2.1  [check21] Ensure CloudTrail is enabled in all regions (Scored)\n\n2.2  [check22] Ensure CloudTrail log file validation is enabled (Scored)\n\n2.3  [check23] Ensure the S3 bucket CloudTrail logs to is not publicly accessible (Scored)\n\n2.4  [check24] Ensure CloudTrail trails are integrated with CloudWatch Logs (Scored)\n\n2.5  [check25] Ensure AWS Config is enabled in all regions (Scored)\n\n2.6  [check26] Ensure S3 bucket access logging is enabled on the CloudTrail S3 bucket (Scored)\n\n2.7  [check27] Ensure CloudTrail logs are encrypted at rest using KMS CMKs (Scored)\n\n2.8  [check28] Ensure rotation for customer created KMS CMKs is enabled (Scored)\n\n2.9  [check29] Ensure VPC Flow Logging is Enabled in all VPCs (Scored)', '[{\"key\":\"group\",\"name\":\"扫描分组\",\"defaultValue\":\"group2\",\"required\":true}]', 'fit2cloud-aws-plugin', 'AWS 亚马逊云', 'aws.png', concat(unix_timestamp(now()), '012'), 1, 'prowler');
