
UPDATE `rule` SET `name` = 'Tencent SecurityGroup高危安全组扫描', `status` = 1, `severity` = 'HighRisk', `description` = 'Tencent  检测安全组是否开启风险端口，未开启视为“合规”，否则属于“不合规”', `script` = 'policies:\n  #扫描开放以下高危端口的安全组：\n  #(20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500)\n  - name: tencent-security-group\n    resource: tencent.security-group\n    description: |\n      Add Filter all security groups, filter ports\n      [20,21,22,25,80,773,765,1733,1737,3306,3389,7333,5732,5500]\n      on 0.0.0.0/0 or\n      [20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\n      on ::/0 (IPv6)\n    filters:\n        - or:\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv4_port}}\n              Cidr: \"0.0.0.0/0\"\n              Action: ${{action}}\n            - type: ingress\n              IpProtocol: \"-1\"\n              Ports: ${{ipv6_port}}\n              CidrV6: \"::/0\"\n              Action: ${{action}}', `parameter` = '[{\"key\":\"ipv4_port\",\"name\":\"ipv4端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true},{\"key\":\"ipv6_port\",\"name\":\"ipv6端口\",\"defaultValue\":\"[20,21,22,25,80,773,765, 1733,1737,3306,3389,7333,5732,5500]\",\"required\":true},{\"key\":\"action\",\"name\":\"状态: ACCEPT 或 DROP\",\"defaultValue\":\"ACCEPT\",\"required\":true}]', `plugin_id` = 'fit2cloud-qcloud-plugin', `plugin_name` = '腾讯云', `plugin_icon` = 'qcloud.png', `last_modified` = 1629360350003, `flag` = 1, `scan_type` = 'custodian' WHERE `id` = Cast('f9d3db46-d340-4487-ae9b-82b827108912' AS Binary(36));
